
public class 문자열_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="hello";
		String s2="HELLO";
		if(s1.equals(s2)) { // 같은 경우 > true / 다른 경우 > false
			System.out.println("s1과 s2가 같다");
		}
		else {
			System.out.println("s1과 s2가 다르다");
		}
	}

}
  